const crypto = require('crypto');
const input = $json;
const now = new Date().toISOString();
const runId = input.run_id || `run_${crypto.randomUUID()}`;
const n8nBaseUrl = $env.N8N_BASE_URL || 'http://localhost:5678';

// Initialize manifest
const manifest = {
  manifest_version: '2.0',
  run_id: runId,
  mode: input.mode || 'test',
  video_type: input.video_type || 'normal',

  topic: {
    title: input.selected_topic || input.custom_topic || '',
    category: input.category || 'education',
    language: input.language || 'en',
    keywords: input.keywords || [],
    summary: '',
    source_references: []
  },

  script: {
    draft: '',
    approved: '',
    review_notes: [],
    last_reviewed_at: null
  },

  assets: {
    scene_plan: [],
    items: [],
    voiceover: {
      provider: 'elevenlabs',
      voice_id: '',
      url: null
    }
  },

  render: {
    resolution: input.quality?.video || '1080p',
    fps: 30,
    render_id: null,
    render_status: 'not_started',
    render_url: null
  },

  thumbnail: {
    url: null,
    variants: []
  },

  publish: {
    channel_id: input.channel_id || '',
    title: '',
    description: '',
    tags: [],
    privacy: 'unlisted',
    youtube_video_id: null,
    publish_status: 'not_published'
  },

  analytics: {
    trending_score: input.trending_score || null,
    estimated_cpm: input.estimated_cpm || null,
    competition_level: input.competition_level || null
  },

  pipeline_status: {
    config: 'pending',
    script: 'pending',
    review: 'pending',
    voice: 'pending',
    assets: 'pending',
    render: 'pending',
    thumbnail: 'pending',
    publish: 'pending'
  },

  audit: {
    created_by: 'wf-01',
    updated_by: 'wf-01',
    change_log: [{
      at: now,
      by: 'wf-01',
      action: 'INIT',
      details: `Pipeline started - mode=${input.mode} type=${input.video_type}`
    }]
  },

  timestamps: {
    created_at: now,
    updated_at: now,
    phase: 'init'
  }
};

return [{
  json: {
    run_id: runId,
    mode: input.mode || 'test',
    video_type: input.video_type || 'normal',
    category: input.category,
    language: input.language || 'en',
    target_countries: input.target_countries || ['US'],
    video_length: input.video_length || {},
    quality: input.quality || {},
    options: input.options || {},
    selected_topic: input.selected_topic || input.custom_topic,
    keywords: input.keywords || [],
    trend_analysis: input.trend_analysis || null,
    manifest: manifest,
    n8n_base_url: n8nBaseUrl,
    status: 'IN_PROGRESS',
    errors: [],
    warnings: []
  }
}];

const prev = $node['Init Pipeline'].json;
const configResult = $json;
const now = new Date().toISOString();

if (configResult.error || !configResult.resolved_config) {
  prev.manifest.pipeline_status.config = 'failed';
  prev.manifest.timestamps.phase = 'failed_config';
  prev.errors.push('Config resolution failed');
  prev.status = 'FAILED_CONFIG';
  prev.halt = true;
  return [{ json: prev }];
}

prev.resolved_config = configResult.resolved_config;
prev.computed = configResult.computed;
prev.manifest.pipeline_status.config = 'completed';
prev.manifest.audit.change_log.push({
  at: now, by: 'wf-01', action: 'CONFIG_LOADED',
  details: `Config source: ${configResult.config_source}`
});
prev.halt = false;

return [{ json: prev }];

const prev = $node['Handle Config'].json;
const scriptResult = $json;
const now = new Date().toISOString();

if (scriptResult.error || scriptResult.status === 'FAILED') {
  prev.manifest.pipeline_status.script = 'failed';
  prev.manifest.timestamps.phase = 'failed_script';
  prev.errors.push(scriptResult.error_message || 'Script generation failed');
  prev.status = 'FAILED_SCRIPT';
  prev.halt = true;
  return [{ json: prev }];
}

prev.manifest.script.draft = scriptResult.script_draft || '';
prev.manifest.assets.scene_plan = scriptResult.scene_plan || [];
prev.manifest.pipeline_status.script = 'completed';
prev.manifest.audit.change_log.push({
  at: now, by: 'wf-03', action: 'SCRIPT_GENERATED',
  details: `Script length: ${(scriptResult.script_draft || '').length} chars`
});
prev.halt = false;

return [{ json: prev }];

const prev = $node['Handle Script'].json;
const reviewResult = $json;
const now = new Date().toISOString();

if (reviewResult.error || reviewResult.status === 'FAILED') {
  prev.manifest.pipeline_status.review = 'failed';
  prev.errors.push('Script review failed');
  prev.status = 'FAILED_REVIEW';
  prev.halt = true;
  return [{ json: prev }];
}

prev.manifest.script.approved = reviewResult.script_approved || prev.manifest.script.draft;
prev.manifest.script.review_notes = reviewResult.review_notes || [];
prev.manifest.script.last_reviewed_at = now;
prev.manifest.publish.title = reviewResult.youtube_title || prev.manifest.topic.title;
prev.manifest.publish.description = reviewResult.youtube_description || '';
prev.manifest.publish.tags = reviewResult.youtube_tags || [];
prev.manifest.pipeline_status.review = 'completed';
prev.manifest.audit.change_log.push({ at: now, by: 'wf-04', action: 'SCRIPT_APPROVED' });
prev.halt = false;

return [{ json: prev }];

const prev = $node['Handle Review'].json;
const voiceResult = $json;
const now = new Date().toISOString();

if (voiceResult.error || voiceResult.status === 'FAILED') {
  prev.manifest.pipeline_status.voice = 'failed';
  prev.errors.push('Voiceover generation failed');
  prev.status = 'FAILED_VOICE';
  prev.halt = true;
  return [{ json: prev }];
}

// FIXED: Use voiceover_url_signed for contract consistency
const voiceUrl = voiceResult.voiceover_url_signed || voiceResult.voiceover_url;
prev.manifest.assets.voiceover.url = voiceUrl;
prev.manifest.assets.voiceover.url_signed = voiceUrl;
prev.manifest.assets.voiceover.voice_id = voiceResult.voice_id;
prev.manifest.assets.voiceover.duration_estimate = voiceResult.audio_duration_estimate;
prev.voiceover_url_signed = voiceUrl;
prev.voiceover_url = voiceUrl;
prev.manifest.pipeline_status.voice = 'completed';
prev.manifest.audit.change_log.push({ at: now, by: 'wf-05', action: 'VOICEOVER_GENERATED' });
prev.halt = false;

return [{ json: prev }];

const prev = $node['Handle Voice'].json;
const assetsResult = $json;
const now = new Date().toISOString();

if (assetsResult.error || assetsResult.status === 'FAILED') {
  prev.manifest.pipeline_status.assets = 'failed';
  prev.errors.push('Asset collection failed');
  prev.status = 'FAILED_ASSETS';
  prev.halt = true;
  return [{ json: prev }];
}

prev.manifest.assets.items = assetsResult.assets || [];
prev.scene_assets = assetsResult.assets || [];
prev.manifest.pipeline_status.assets = 'completed';
prev.manifest.audit.change_log.push({
  at: now, by: 'wf-06', action: 'ASSETS_COLLECTED',
  details: `${(assetsResult.assets || []).length} assets`
});
prev.halt = false;

return [{ json: prev }];

const prev = $node['Handle Assets'].json;
const renderResult = $json;
const now = new Date().toISOString();

if (renderResult.error || renderResult.status === 'FAILED') {
  prev.manifest.pipeline_status.render = 'failed';
  prev.errors.push('Video render failed');
  prev.status = 'FAILED_RENDER';
  prev.halt = true;
  return [{ json: prev }];
}

prev.manifest.render.render_id = renderResult.render_id;
prev.manifest.render.render_url = renderResult.render_url;
prev.manifest.render.render_status = 'completed';
prev.render_url = renderResult.render_url;
prev.manifest.pipeline_status.render = 'completed';
prev.manifest.audit.change_log.push({ at: now, by: 'wf-07', action: 'RENDER_COMPLETED' });
prev.halt = false;

return [{ json: prev }];

const prev = $node['Handle Render'].json;
const thumbResult = $json;
const now = new Date().toISOString();

// Thumbnail failure is non-critical
if (thumbResult.error || thumbResult.status === 'FAILED') {
  prev.manifest.pipeline_status.thumbnail = 'failed';
  prev.warnings.push('Thumbnail generation failed - using default');
} else {
  prev.manifest.thumbnail.url = thumbResult.thumbnail_url;
  prev.manifest.thumbnail.variants = thumbResult.variants || [];
  prev.thumbnail_url = thumbResult.thumbnail_url;
  prev.manifest.pipeline_status.thumbnail = 'completed';
  prev.manifest.audit.change_log.push({ at: now, by: 'wf-08', action: 'THUMBNAIL_GENERATED' });
}

prev.halt = false;
return [{ json: prev }];

const prev = $node['Handle Thumbnail'].json;
const publishResult = $json;
const now = new Date().toISOString();

if (publishResult.error || publishResult.status === 'FAILED') {
  prev.manifest.pipeline_status.publish = 'failed';
  prev.errors.push('YouTube publish failed');
  prev.status = 'FAILED_PUBLISH';
} else {
  prev.manifest.publish.youtube_video_id = publishResult.youtube_video_id;
  prev.manifest.publish.publish_status = 'published';
  prev.youtube_video_id = publishResult.youtube_video_id;
  prev.manifest.pipeline_status.publish = 'completed';
  prev.manifest.audit.change_log.push({
    at: now, by: 'wf-09', action: 'PUBLISHED',
    details: `YouTube ID: ${publishResult.youtube_video_id}`
  });
  prev.status = 'COMPLETED';
}

prev.manifest.timestamps.updated_at = now;
prev.manifest.timestamps.phase = 'completed';

return [{ json: prev }];

const prev = $json;
const now = new Date().toISOString();

// Test mode - skip publish, mark as test complete
prev.manifest.pipeline_status.publish = 'skipped';
prev.manifest.audit.change_log.push({
  at: now, by: 'wf-01', action: 'TEST_COMPLETED',
  details: 'Test mode - publish skipped'
});
prev.manifest.timestamps.updated_at = now;
prev.manifest.timestamps.phase = 'test_completed';
prev.status = 'TEST_COMPLETED';

return [{ json: prev }];

const result = $json;
const now = new Date().toISOString();

// Update database with final status
const finalResponse = {
  run_id: result.run_id,
  status: result.status,
  mode: result.mode,
  video_type: result.video_type,

  // URLs
  render_url: result.render_url,
  thumbnail_url: result.thumbnail_url,
  youtube_video_id: result.youtube_video_id,

  // Pipeline status
  pipeline_status: result.manifest?.pipeline_status,

  // Errors/Warnings
  errors: result.errors || [],
  warnings: result.warnings || [],

  // Full manifest for reference
  manifest: result.manifest,

  completed_at: now
};

return [{ json: finalResponse }];

// Return failure response
const result = $json;
result.manifest.timestamps.updated_at = new Date().toISOString();
return [{ json: result }];
