const input = $json;
const config = input.resolved_config || {};
const metadata = input.manifest?.metadata || {};
const isTest = input.mode === 'test';

// Validate required fields
const renderUrl = input.manifest?.render_url;
if (!renderUrl) {
  return [{
    json: {
      ...input,
      error: true,
      error_message: 'No render URL available for publishing',
      status: 'FAILED'
    }
  }];
}

// Build video metadata
const title = metadata.title || input.selected_topic || 'Untitled Video';
const description = metadata.description || '';
const tags = metadata.tags || [];
const categoryId = metadata.category_id || '22'; // People & Blogs

// Determine privacy status
// Test mode = private, Production = public or scheduled
let privacyStatus = 'private';
let publishAt = null;

if (!isTest && input.options?.best_time_publish) {
  // Calculate best publish time (e.g., next day at optimal hour)
  const optimalHour = config.publishing?.optimal_hour || 14; // 2 PM
  const publishDate = new Date();
  publishDate.setDate(publishDate.getDate() + 1);
  publishDate.setHours(optimalHour, 0, 0, 0);
  privacyStatus = 'private';
  publishAt = publishDate.toISOString();
} else if (!isTest) {
  privacyStatus = 'public';
}

// Build YouTube API request
const youtubeMetadata = {
  snippet: {
    title: title.substring(0, 100),
    description: description.substring(0, 5000),
    tags: tags.slice(0, 500),
    categoryId: categoryId,
    defaultLanguage: input.language || 'en',
    defaultAudioLanguage: input.language || 'en'
  },
  status: {
    privacyStatus: privacyStatus,
    selfDeclaredMadeForKids: false,
    publishAt: publishAt
  }
};

return [{
  json: {
    ...input,
    youtube_metadata: youtubeMetadata,
    render_url: renderUrl,
    thumbnail_url: input.manifest?.thumbnail_url,
    is_test: isTest,
    privacy_status: privacyStatus,
    scheduled_publish: publishAt
  }
}];

const input = $json;
const now = new Date().toISOString();

// In test mode, skip actual upload
return [{
  json: {
    run_id: input.run_id,
    mode: input.mode,
    youtube_video_id: null,
    youtube_url: null,
    render_url: input.render_url,
    thumbnail_url: input.thumbnail_url,
    status: 'TEST_COMPLETE',
    test_result: {
      would_upload: true,
      title: input.youtube_metadata.snippet.title,
      description_length: input.youtube_metadata.snippet.description.length,
      tags_count: input.youtube_metadata.snippet.tags.length,
      privacy: input.privacy_status,
      scheduled: input.scheduled_publish
    },
    completed_at: now
  }
}];

const input = $node['Prepare Upload Metadata'].json;
const uploadResponse = $json;
const now = new Date().toISOString();

try {
  if (uploadResponse.error || !uploadResponse.id) {
    throw new Error(uploadResponse.error?.message || uploadResponse.message || 'Upload failed');
  }

  const videoId = uploadResponse.id;
  const youtubeUrl = `https://youtube.com/watch?v=${videoId}`;

  return [{
    json: {
      run_id: input.run_id,
      mode: input.mode,
      youtube_video_id: videoId,
      youtube_url: youtubeUrl,
      youtube_status: uploadResponse.status,
      privacy_status: input.privacy_status,
      scheduled_publish: input.scheduled_publish,
      thumbnail_url: input.thumbnail_url,
      has_thumbnail: !!input.thumbnail_url,
      uploaded_at: now,
      status: 'PUBLISHED'
    }
  }];

} catch (error) {
  return [{
    json: {
      run_id: input.run_id,
      mode: input.mode,
      error: true,
      error_message: error.message,
      status: 'FAILED'
    }
  }];
}

const input = $json;
const now = new Date().toISOString();

// Final response - with or without thumbnail
return [{
  json: {
    run_id: input.run_id,
    mode: input.mode,
    youtube_video_id: input.youtube_video_id,
    youtube_url: input.youtube_url,
    privacy_status: input.privacy_status,
    scheduled_publish: input.scheduled_publish,
    thumbnail_set: true,
    completed_at: now,
    status: 'PUBLISHED'
  }
}];

const input = $json;
const now = new Date().toISOString();

// Final response without thumbnail
return [{
  json: {
    run_id: input.run_id,
    mode: input.mode,
    youtube_video_id: input.youtube_video_id,
    youtube_url: input.youtube_url,
    privacy_status: input.privacy_status,
    scheduled_publish: input.scheduled_publish,
    thumbnail_set: false,
    completed_at: now,
    status: input.error ? 'FAILED' : 'PUBLISHED'
  }
}];
