const crypto = require('crypto');
const input = $json;
const now = new Date().toISOString();
const runId = `run_${crypto.randomUUID()}`;

const keywords = String(input.keywords_raw || '')
  .split(',')
  .map((item) => item.trim())
  .filter((item) => item.length > 0);

let runtimeOverrides = {};
if (input.runtime_overrides_json) {
  runtimeOverrides = JSON.parse(input.runtime_overrides_json);
}

const manifest = {
  manifest_version: '2.0',
  topic: {
    title: input.title,
    category: input.category,
    language: input.language,
    keywords,
    summary: input.summary,
    source_references: []
  },
  script: {
    draft: '',
    approved: '',
    review_notes: [],
    last_reviewed_at: null
  },
  assets: {
    scene_plan: [],
    items: [],
    voiceover: {
      provider: '',
      voice_id: '',
      speed: 1.0,
      pitch: 0.0,
      output_format: 'mp3',
      voiceover_url_signed: null
    }
  },
  render: {
    resolution: '1920x1080',
    fps: 30,
    codec: 'h264',
    bitrate_kbps: 8000,
    render_id: null,
    render_status: 'not_started'
  },
  publish: {
    channel_id: input.channel_id || '',
    title: '',
    description: '',
    tags: [],
    privacy: 'unlisted',
    scheduled_at: null,
    youtube_video_id: null,
    publish_status: 'not_published'
  },
  analytics: {
    enabled: true,
    tracking_tags: [],
    first_publish_at: null,
    last_metrics_pull_at: null,
    metrics: {}
  },
  final: {
    status: '',
    summary: '',
    completed_at: null
  },
  audit: {
    created_by: 'wf-01',
    updated_by: 'wf-01',
    change_log: [
      {
        at: now,
        by: 'wf-01',
        change: `step=init status=ok run_id=${runId} mode=${input.mode}`
      }
    ]
  },
  timestamps: {
    created_at: now,
    updated_at: now,
    phase: 'ideation'
  }
};

return [
  {
    json: {
      run_id: runId,
      mode: input.mode,
      scene_assets: [],
      voiceover_url_signed: null,
      render_id: null,
      youtube_video_id: null,
      manifest,
      environment: input.environment,
      scope: input.scope,
      channel_id: input.channel_id || null,
      runtime_overrides: runtimeOverrides,
      status: 'IN_PROGRESS'
    }
  }
];

const init = $node['Init Contract'].json;
const response = $json;
const now = new Date().toISOString();

const failed = !response || response.error || response.statusCode >= 400 || !response.resolved_config;

if (failed) {
  init.manifest.timestamps.phase = 'failed_preconfig';
  init.manifest.timestamps.updated_at = now;
  init.manifest.final.status = 'FAILED_PRECONFIG';
  init.manifest.final.summary = 'wf-10 config resolve failed';
  init.manifest.final.completed_at = now;
  init.status = 'FAILED_PRECONFIG';
  init.manifest.audit.change_log.push({
    at: now,
    by: 'wf-01',
    change: `step=wf-10 status=FAILED error_code=FAILED_PRECONFIG error_message=wf-10-unavailable run_id=${init.run_id} mode=${init.mode}`
  });
  return [
    {
      json: {
        ...init,
        resolved_config: null,
        halt: true
      }
    }
  ];
}

init.manifest.audit.change_log.push({
  at: now,
  by: 'wf-01',
  change: `step=wf-10 status=COMPLETED run_id=${init.run_id} mode=${init.mode}`
});
init.manifest.timestamps.updated_at = now;

return [
  {
    json: {
      ...init,
      resolved_config: response.resolved_config,
      halt: false
    }
  }
];

